mapper.update:
UPDATE vital_signs
height=#{height}, weight=#{weight}, blood_pressure=#{bloodPressure}, blood_sugar=#{bloodSugar}, blood_lipids=#{bloodLipids}, note=#{note}
record_id=#{id} and user_id=#{userId}

mapper.select:
select * from vital_signs

    user_id=#{userId}
    and note like concat('%',#{keyword},'%') 

order by created_at limit #{startIndex},#{limit}
